// --------------------------------------------------------
// cim.api :: CIM services proxy.
// N.B. Declared in a functional closure so as not to pollute global namespace.
// N.B. Executes remote CIM web services.
// --------------------------------------------------------

// --------------------------------------------------------
// cim.api :: Extend main cim object with set of api proxies.
// --------------------------------------------------------
cim.extend('api', (function() {
    // Create self reference.
    var api = {};

    // Services proxy default error handler.
    // @exception       Exception that has occurred processing a request.
    // @cim_version     CIM version being rendered.
    // @service         Service being invoked.
    // @operation       Service operation being invoked.
    // @onError         Callback to invoke when function unsucessfully executes.
    var onServiceError = function(exception, cim_version, service, operation, onError) {
        var error;      // Error message to be returned.
        
        // Either invoke error handler or default tasks.
        if (_.isFunction(onError) === true) {
            onError(exception, cim_version, service, operation);
        } else {
            error = "CIM v{0} web service processing error:\n".replace('{0}', cim_version);
            error += "\tService\t: "+ service + "\n";
            error += "\tOperation\t: "+ operation + "\n";
            error += "\tError\t\t: " + exception.message + "\n";
            alert(error);
        }
    };

    // CIM Version 1.5 service proxy.
    api.v1_5 = {
        // Helper functionS.
        helpers : {
            getRepository : function (cimType) {
                return cim.repository['1.5'][cimType.toUpperCase()];
            }
        },

        // CIM Version 1.5 query service proxy.
        query : {
            // Gets a cim document by drs elements.
            // @project         Project with which document is associated.
            // @cim_type        CIM instance type being rendered.
            // @name            Short Name of document being returned.
            // @onSuccess       Callback to invoke when function sucessfully executes.
            // @onError         Callback to invoke when function unsucessfully executes.
            getByDRS : function(project, cim_type, name, onSuccess, onError) {
                var result;      // Instance to be returned.
                var repo;        // Repo to be searched.

                // Search against CIM repository.
                try {
                    // Set repo.
                    repo = api.v1_5.helpers.getRepository(cim_type)

                    // Format params.
                    name = $jq.trim(name);
                    name = name.toUpperCase();

                    // Search by name.
                    result = _(repo).detect(function(i) {
                        return i.shortName.toUpperCase() === name;
                    });

                    // If not found return first.
                    if (result === undefined) {
                        result = _.first(repo);
                    }
                }
                catch(exception) {
                    onServiceError(exception, '1.5', 'query', 'getByName', onError);
                }

                // Invoke callback (if specified).
                if (_.isFunction(onSuccess) === true) {
                    onSuccess(result);
                }

                return result;
            },

            // Gets a cim document by id / version.
            // @project         Project with which document is associated.
            // @cim_type        CIM instance type being rendered.
            // @name            Short Name of document being returned.
            // @onSuccess       Callback to invoke when function sucessfully executes.
            // @onError         Callback to invoke when function unsucessfully executes.
            getByID : function(project, cim_type, name, onSuccess, onError) {
                var result;      // Instance to be returned.
                var repo;        // Repo to be searched.

                // Search against CIM repository.
                try {
                    // Set repo.
                    repo = api.v1_5.helpers.getRepository(cim_type)

                    // Search by name.
                    result = _(repo).detect(function(i) {
                        return i.shortName.toUpperCase() === name.toUpperCase();
                    });

                    // If not found search by synonym.
                    if (result === undefined) {
                        result = _(repo).detect(function(i) {
                            return i.synonyms.toUpperCase().search(name.toUpperCase()) !== -1;
                        });
                    }

                    // If not found return first.
                    if (result === undefined) {
                        result = _.first(repo);
                    }
                }
                catch(exception) {
                    onServiceError(exception, '1.5', 'query', 'getByName', onError);
                }

                // Invoke callback (if specified).
                if (_.isFunction(onSuccess) === true) {
                    onSuccess(result);
                }

                return result;
            },

            // Gets a cim document by name.
            // @project         Project with which document is associated.
            // @cim_type        CIM instance type being rendered.
            // @name            Short Name of document being returned.
            // @onSuccess       Callback to invoke when function sucessfully executes.
            // @onError         Callback to invoke when function unsucessfully executes.
            getByName : function(project, cim_type, name, onSuccess, onError) {
                var result;      // Instance to be returned.
                var repo;        // Repo to be searched.

                // Search against CIM repository.
                try {
                    // Set repo.
                    repo = api.v1_5.helpers.getRepository(cim_type)

                    // Format params.
                    name = $jq.trim(name);
                    name = name.toUpperCase();

                    // Search by name.
                    result = _(repo).detect(function(i) {
                        return i.shortName.toUpperCase() === name;
                    });

                    // If not found return first.
                    if (result === undefined) {
                        result = _.first(repo);
                    }
                }
                catch(exception) {
                    onServiceError(exception, '1.5', 'query', 'getByName', onError);
                }

                // Invoke callback (if specified).
                if (_.isFunction(onSuccess) === true) {
                    onSuccess(result);
                }

                return result;
            }
        }
    };

    // Return constructed object.
    return api;
}()));

